//Logo and search icon imports
import logo from "../../assets/Logo_ML.png";
import searchIcon from "../../assets/ic_Search.png";

//Material UI
import * as React from 'react';
import { styled, alpha } from '@mui/material/styles';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import InputBase from '@mui/material/InputBase';

import { Link, Navigate } from "react-router-dom";

//Component CSS
import "./SearchBar.css";

//Material UI CSS
const Search = styled('div')(({ theme }) => ({
  backgroundColor: '#FFFFFF',
  borderBottomRightRadius: '4px',
  borderTopRightRadius: '4px',
  borderRadius: theme.shape.borderRadius,
  color: '#000',
  marginLeft: 0,
  position: 'relative',
  width: '100%',
  [theme.breakpoints.up('sm')]: {
    marginLeft: theme.spacing(1),
    width: 'auto',
  },
  width: '500px',
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
  alignItems: 'center',
  backgroundColor: '#EEEEEE',
  "&:hover":'#FFFFFF',
  borderBottomRightRadius: '4px',
  borderTopRightRadius: '4px',
  display: 'flex',
  height: '100%',
  justifyContent: 'center',
  padding: theme.spacing(0, 1.5),
  pointerEvents: 'none',
  position: 'absolute',
  right: '0px',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: '#333333',
  ':placeholder': {
    color: "#999999"
  },
  width: '800px',

  '& .MuiInputBase-input': {
    padding: theme.spacing(1, 1, 1, 0),
    fontSize: "18px",
    // vertical padding + font size from searchIcon
    paddingLeft: '10px',
    paddingRight: `calc(1em + ${theme.spacing(4)})`,
    width: '100%'
  },
}));


function SearchBar() {
  const handleClick = () => {
    navigate("/items/search")
  }
  return (
    <Box sx={{ flexGrow: 1 }}>
      {/* These inline styles should be moved */}
      <AppBar position="static" style={{ background: '#FFE600', boxShadow: 'none' }}>
        <Toolbar style={{ margin: 'auto' }}>
          <img src={logo} className="App-logo" alt="logo" style={{ marginRight: '20px' }} />
          <Search>
            <SearchIconWrapper class="search-button" onClick={handleClick}>
              <img src={searchIcon} />
            </SearchIconWrapper>
            <StyledInputBase
              placeholder="Nunca dejes de buscar"
              inputProps={{ 'aria-label': 'search' }}
              className="search-box"
            />
          </Search>
        </Toolbar>
      </AppBar>
    </Box>
  );
}
export default SearchBar;
